mapper.select:
select aa.person_name ,aa.card_code ,aa.card_phone, aa.account_fee,aa.company_name , aa.area_name ,aa.state ,aa.create_time,aa.creator, aa.temp_str1,aa.card_sfz from ( select ab.* ,rownum row_no from ( SELECT cc.person_name ,cc.card_code ,cc.phone card_phone, (select text_ from act_hi_varinst where proc_inst_id_ = a2.proc_inst_id_ and name_='card_points' ) account_fee, decode((select text_ from act_hi_varinst where proc_inst_id_ = a2.proc_inst_id_ and name_='state' ) ,'in_storge','有效' ,'无效' ) state , (select text_ from act_hi_varinst where proc_inst_id_ = a2.proc_inst_id_ and name_='create_time' ) create_time, (select staff_name from act_hi_varinst,staff where proc_inst_id_ = a2.proc_inst_id_ and name_='creator' and text_=fdid ) creator, (select company_name from act_hi_varinst,staff a,staff2company sc,company cp where proc_inst_id_ = a2.proc_inst_id_ and name_='creator' and text_=a.fdid and a.fdid = sc.staff_id and sc.company_id = cp.fdid ) company_name, (select area_name from act_hi_varinst,staff a,staff2company sc,company cp,area aa where proc_inst_id_ = a2.proc_inst_id_ and name_='creator' and text_=a.fdid and a.fdid = sc.staff_id and sc.company_id = cp.fdid and aa.area_code = cp.area_code ) area_name, a2.proc_inst_id_ temp_str1,cc.sfz card_sfz FROM act_hi_varinst a1,act_hi_varinst a2 ,card cc where a1.proc_inst_id_ = a2.proc_inst_id_ and a2.name_ ='card_code' and a2.text_ = cc.fdid and a1.name_='state' and a1.text_ = 'in_storge' order by create_time desc ) ab ) aa where 1=1
and aa.person_name like '%'||#{person_name}||'%'
and to_char(create_time,'yyyy-mm-dd') = #{create_time}
and company_name like '%'||#{company_name}||'%'
and row_no between #{temp_str1} and #{temp_str2}

select aa.person_name ,aa.card_code ,aa.card_phone, aa.account_fee,aa.company_name ,aa.company_id, aa.area_name ,aa.state ,aa.create_time,aa.creator, aa.temp_str1,aa.card_sfz from ( select ab.* ,rownum row_no from ( SELECT cc.person_name ,cc.card_code ,cc.phone card_phone, (select text_ from act_hi_varinst where proc_inst_id_ = a2.proc_inst_id_ and name_='card_points' ) account_fee, decode((select text_ from act_hi_varinst where proc_inst_id_ = a2.proc_inst_id_ and name_='state' ) ,'in_storge','有效' ,'无效' ) state , (select text_ from act_hi_varinst where proc_inst_id_ = a2.proc_inst_id_ and name_='create_time' ) create_time, (select staff_name from act_hi_varinst,staff where proc_inst_id_ = a2.proc_inst_id_ and name_='creator' and text_=fdid ) creator, (select company_name from act_hi_varinst,staff a,staff2company sc,company cp where proc_inst_id_ = a2.proc_inst_id_ and name_='creator' and text_=a.fdid and a.fdid = sc.staff_id and sc.company_id = cp.fdid ) company_name, (select cp.fdid from act_hi_varinst,staff a,staff2company sc,company cp where proc_inst_id_ = a2.proc_inst_id_ and name_='creator' and text_=a.fdid and a.fdid = sc.staff_id and sc.company_id = cp.fdid ) company_id , (select area_name from act_hi_varinst,staff a,staff2company sc,company cp,area aa where proc_inst_id_ = a2.proc_inst_id_ and name_='creator' and text_=a.fdid and a.fdid = sc.staff_id and sc.company_id = cp.fdid and aa.area_code = cp.area_code ) area_name, a2.proc_inst_id_ temp_str1,cc.sfz card_sfz FROM act_hi_varinst a1,act_hi_varinst a2 ,card cc where a1.proc_inst_id_ = a2.proc_inst_id_ and a2.name_ ='card_code' and a2.text_ = cc.fdid and a1.name_='state' and a1.text_ = 'in_storge' order by create_time desc ) ab ) aa where 1=1
and aa.person_name like '%'||#{person_name}||'%'
and to_char(create_time,'yyyy-mm-dd') = #{create_time}
and company_id = #{company_id}
and aa.card_phone = #{card_phone}
and row_no between #{temp_str1} and #{temp_str2}
